package c;
//循环输出10以内的整数（for）
public class c28 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<10;i++) {
			System.out.println(i);
		}
		int sum=0;
		int j=1;
		for(;j<=10;j++) {
			sum=sum+j;//
		}
		System.out.println("j="+j+"sum="+sum);
	}

}
